document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".menu-toggle"),t=document.querySelector(".nav-menu");if(e&&t){e.addEventListener("click",function(){e.classList.toggle("active"),t.classList.toggle("active"),document.body.style.overflow=t.classList.contains("active")?"hidden":""});const n=document.querySelectorAll(".nav-links a");n.forEach(n=>{n.addEventListener("click",()=>{e.classList.remove("active"),t.classList.remove("active"),document.body.style.overflow=""})}),document.addEventListener("click",function(n){!e.contains(n.target)&&!t.contains(n.target)&&(e.classList.remove("active"),t.classList.remove("active"),document.body.style.overflow="")})}const n=document.querySelectorAll(".project-card.placeholder .button");n.forEach(e=>{e.addEventListener("click",function(e){e.stopPropagation()})})})